(function($){$.fn.sTabs=function(options){var opts=$.extend({},$.fn.sTabs.defaults,options);return this.each(function(){$(this).addClass('tabs');$(this).find('a').each(function(){$($(this).attr('href')).addClass('tab').hide();$(this).click(function(){$(this).addClass('active');opts.animate ? $($(this).attr('href')).fadeIn(opts.duration) : $($(this).attr('href')).show();$($(this).parent().siblings().find('a')).each(function(){$(this).removeClass('active');$($(this).attr('href')).hide();});return false;})});var first=$(this).find('li:nth-child('+opts.startWith+')').children('a');$(first).addClass('active');$($(first).attr('href')).show();});};$.fn.sTabs.defaults={animate:false,duration:300,startWith:1};})(jQuery);